                font-weight: 500;
                line-height: 90%;
                text-align: center;
                opacity: 1;
                mix-blend-mode: difference;
            }

            .underline {
                opacity: 1 !important;
            }

            .inner-nav {
                font-weight: lighter;
                li {
                    opacity: 0.6;
                    &:not(:last-child) {
                        margin-right: 2vw;
                    }
                }
            }

            .localization {
                span {
                    &:not(:last-child) {
                        margin-right: 20px;
                    }
                }
            }

        }
    }
}

.Intro {
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    height: 100%;
    overflow: hidden;
    position: relative;

    .side {
        display: none;
        position: absolute;
        top: 50%;
        left: 5%;
        transform: rotate(90deg) translate(-50%, 0);
        width: 500px;
        height: 2px;
        transform-origin: bottom left;

        .timer-line {
            position: absolute;
            width: 0;
            height: 100%;
            background-color: $main-color;
        }

        .timer-line-bg {
            position: absolute;
            background-color: $main-color;
            opacity: 0.1;
            width: 100%;
            height: 100%;
            z-index: -1;
        }
    }

    .intro-main {
        padding-top: 150px;
        overflow: hidden;
        height: 100%;
        position: relative;
        pointer-events: none;

        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        // justify-content: center;

        h1 {
            font-size: 3.5vw;
            // font-size: 96px;
            font-weight: normal;
            letter-spacing: -0.2vw;
            line-height: 1.3;
            margin: 0;
            // position: absolute;
            text-align: center;
            // transform: translate(0, -20%);
            // width: 100%;
            display: inline-block;
            bottom: 5%;
        }

        &::after {
            content: '';
            display: block;
            clear: both;
        }

        > .ImageContainer {
            display: inline-block;
            height: 40%;
            width: 50%;
            img {
                transform: translate(0, -50%);
            }
        }
        > *:not(:first-child) {
            margin-top: 50px;
        }
    }

    .intro-bottom {
        // font-size: 1vw;
        // justify-content: space-between;
        // line-height: 200px;
        // line-height: 200px;
        // max-width: 1920px;
        // position: absolute;
        // position: absolute;
        // text-align: center;
        background-color: $main-dark;
        align-items: center;
        bottom: 0;
        display: flex;
        flex-direction: row;
        font-size: 24px;
        height: 200px;
        margin: 0 auto;
        width: 100%;
        z-index: 99;
        div {
            flex: 1 1 0;
            text-align: center;
        }
    }
}

.TechStack {
    padding: 100px 0;
    text-align: center;
    > *:not(:first-child) {
        margin-top: 100px;
    }

    span {
        font-size: 18px;
        display: block;
    }

    img {
        // margin-top: 50px;
        max-width: 1300px;
        width: 80%;
    }
}

.Project {
    padding: 100px 0;
    // letter-spacing: -3px;
    // margin-bottom: 300px;
    overflow: hidden;
    .container > div:not(:first-child) {
        margin-top: 120px;
    }

    .ProjectHeader {
        margin: 0 auto;
        display: flex;
        justify-content: space-between;
    }

    .ProjectDetailTitle {
        font-size: 28px;
        // font-size: 2vw;
        color: $main-color;
        opacity: 0.3;
        display: block;
        // margin-bottom: 20px;
        font-weight: bold;
        margin-bottom: 25px;
        letter-spacing: 0.5px !important;
    }

    .ProjectDetailValue {
        font-size: 48px;
        font-size: 2vw;
    }

    .ProjectDescription {
        // letter-spacing: -3px;
        max-width: 1920px;
        font-size: 96px;
        font-size: 4vw;
        // margin: 100px 270px 0 270px;
        line-height: 1.3;
        width: 90%;
    }

    .Project-gallery {

        > div {
            margin-top: 120px;
            &:first-of-type {
                .Project-comment {
                    width: 40%;
                }
            }
        }

        .Project-comment {
            display: inline-block;
            // font-size: 28px;
            font-size: 1.1vw;
            font-weight: 300;
            line-height: 180%;
        }
        .Project-comment:nth-child(3) {
            // color: red;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: space-around;
            flex: 1 1 0;
            // font-size: 1vw;
            padding: 50px 0;
            // color: red;
            p {
                font-weight: 300;
                line-height: 200%;
            }

        }

        .row {
            > div {
                display: flex;
                flex-direction: column;
                // justify-content: space-between;

                p {
                    // margin-top: 100px;
                    align-self: bottom;
                    // flex: 1 1 0;
                    // flex-grow: 1;
                    // line-height: 300%;
                }
            }
        }

        .ImageContainer {
            width: 100%;
        }
    }

    &::after {
        content: '';
        clear: both;
        display: block;
    }
}

.MoreWillCome {
    padding: 100px 0;
    text-align: center;
    font-size: 3vw;
}

.Gallery {
    display: block;
    float: left;
    position: relative;
    white-space: nowrap;
    &:hover {
        cursor: move;
    }

    &::after {
        content: '';
        display: block;
        clear: both;
    }

    .ImageContainer {
        display: inline-block;
        height: 80vh;

        img {
            height: 100%;
            min-width: 100%;
            width: auto;
        }
    }
    .ImageContainer:not(:last-child) {
        margin-right: 60px;

    }
}

.GetInTouch {
    font-size: 24px;
    margin: 100px auto 0;
    .availability {
        span {
            margin-top: 20px;
        }
    }

    h1 {
        font-size: 64px;
    }

    span {
        display: block;
        // margin-top: 20px;
    }

    .Dash {
        margin: 50px 0;
        width: 100%;
    }

    .row {
        margin-top: 100px;
        > div {
            flex-grow: 1;
            flex-basis: 0;
            &:not(:last-child) {
                margin-right: 250px;
            }
        }
    }
}

.contact-info {
    ul {
        li {
            display: flex;
            justify-content: space-between;
            padding: 25px 0;
            // width: 500px;
            span {
                display: inline-block;
            }
        }
    }
}

.shortcuts {
    > ul {
        display: flex;
        > li {
            display: flex;
            flex-direction: column;

            span {
                font-weight: 800;
                color: #5F5F5F;
                margin-bottom: 50px;
                font-size: 18px;
            }

            ul {
                font-size: 18px;
                li {
                    &:not(:last-child) {
                        margin-bottom: 20px;
                    }
                }
            }
            &:not(:last-child) {
                margin-right: 50px;
            }
        }
    }
}

.footer-right {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.contact-quote {
    // width: 70%;
    line-height: 160%;
    font-size: 1.1vw;
    &::before {
        content: 'â€œ';
        font-size: 64px;

    }
}

.cta-buttons {
    display: flex;
    justify-content: space-between;
    margin-top: 100px;
    width: 50%;
}

.eof {
    margin-top: 120px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding-bottom: 50px;

    .Dash {
        box-sizing: border-box;
        margin: 0 100px;
    }

    span {
        flex: 0 0 auto;
    }
    span:nth-child(2) {
        text-align: right;
    }
}

